import React from "react";
import "../collection_detail.css";
import bracelet1 from "../images/bracelet1.jpg";
import bracelet2 from "../images/bracelet2.jpg";
import bracelet3 from "../images/bracelet3.jpg";
import bracelet4 from "../images/bracelet4.jpg";
import bracelet5 from "../images/bracelet5.jpg";
import ring1 from "../images/ring1.jpg";
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import { useState, useEffect, useRef } from "react";
import { db } from "../firebase";
import { query, collection, onSnapshot, where } from "firebase/firestore";

function CollectionDetail() {
  let { id } = useParams();

  const [magnifierStyle, setMagnifierStyle] = useState({});
  const imgRef = useRef();

  const handleMouseMove = (e) => {
    const { left, top, width, height } = imgRef.current.getBoundingClientRect();
    const x = e.pageX - left;
    const y = e.pageY - top;
    const magnifierSize = 200;
    const glassStyle = {
      left: `${x - magnifierSize / 2}px`,
      top: `${y - magnifierSize / 2}px`,
      backgroundImage: `url('${imgRef.current.src}')`,
      backgroundSize: `${width * 2}px ${height * 2}px`,
      backgroundPosition: `-${x - magnifierSize / 2}px -${
        y - magnifierSize / 2
      }px`,
      width: `${magnifierSize}px`,
      height: `${magnifierSize}px`,
      position: "absolute",
      borderRadius: "50%",
      border: "2px solid #999",
    };
    setMagnifierStyle(glassStyle);
  };

  const handleMouseOut = () => {
    setMagnifierStyle({});
  };
  const [product_detail, setProduct_detail] = useState([]);

  useEffect(() => {
    const productRef = query(
      collection(db, "products"),
      where("productID", "==", "BRC-001")
    );
    const unsubscribe = onSnapshot(productRef, (QuerySnapshot1) => {
      QuerySnapshot1.forEach((doc) => {
        product_detail.push({ ...doc.data(), id: doc.id });
      });
      setProduct_detail(product_detail);
    });
    return unsubscribe;
  }, []);

  return (
    <div class="row mx-5 my-3">
      <div className="col-6 img-magnifier-container">
        <img
          ref={imgRef}
          src={bracelet1}
          alt="image unavailiable img"
          id="myimage"
          onMouseMove={handleMouseMove}
          onMouseOut={handleMouseOut}
        />
        <div className="img-magnifier-glass" style={magnifierStyle}></div>
      </div>
      <section class="col-6 section">
        {product_detail.map((p) => (
          <div>
            <h6> {p.product_name} - hi</h6>
            <p> {p.description}</p>
            <p> Price : {p.price} MMK</p>
          </div>
        ))}
      </section>

      <div>
        <h5
          style={{
            fontFamily: "Brush Script MT, Amatic SC",
            fontSize: "45px",
            textAlign: "center",
          }}
        >
          Other Jewelry You Might Like
        </h5>
        <div class="row collection1 mx-5">
          <div class="carousel slide " data-bs-ride="carousel" id="collection1">
            <ol class="carousel-indicators">
              <li
                data-bs-target="#collection1"
                data-bs-slide-to="0"
                class="active"
              ></li>
              <li data-bs-target="#collection1" data-bs-slide-to="1"></li>
            </ol>

            <div class="carousel-inner">
              <div class="carousel-item active">
                <div class="row collection_series">
                  <div class="col-lg-3 col-md-4 col-6 card">
                    <img
                      src={bracelet1}
                      alt="image unavailable "
                      class="card-header"
                    ></img>
                    <div class="card-body">
                      <h5> Proudct Name </h5>
                      <p> Description</p>
                      <p> $0.00</p>
                      <Link to="/collection/details" style={{ color: "black" }}>
                        Details
                      </Link>
                    </div>
                  </div>
                  <div class="col-lg-3 col-md-4 col-6 card">
                    <img
                      src={bracelet2}
                      alt="image unavailable "
                      class="card-header"
                    ></img>
                    <div class="card-body">
                      <h5> Proudct Name </h5>
                      <p> Description</p>
                      <p> $0.00</p>
                      <Link to="/collection/details" style={{ color: "black" }}>
                        {" "}
                        Details
                      </Link>
                    </div>
                  </div>
                  <div class="col-lg-3 col-md-4 col-6 card">
                    <img
                      src={bracelet3}
                      alt="image unavailable "
                      class="card-header"
                    ></img>
                    <div class="card-body">
                      <h5> Proudct Name </h5>
                      <p> Description</p>
                      <p> $0.00</p>{" "}
                      <Link to="/collection/details" style={{ color: "black" }}>
                        {" "}
                        Details
                      </Link>
                    </div>
                  </div>
                  <div class="col-lg-3 col-md-4 col-6 card">
                    <img
                      src={bracelet4}
                      alt="image unavailable "
                      class="card-header"
                    ></img>
                    <div class="card-body">
                      <h5> Proudct Name </h5>
