import React from "react";
import { useParams } from "react-router-dom";
import { Link, useLocation } from "react-router-dom";
import "../collection.css";
import { useState, useEffect } from "react";
import { db } from "../firebase";
import { query, collection, onSnapshot, where } from "firebase/firestore";
import { Carousel } from "react-bootstrap";

function Collection() {
  const { linkValue } = useParams();
  var [categories, setCategories] = useState([]);
  var [products, setProducts] = useState([]);
  var [filteredCategoryId, setfilteredCategories] = useState(null);

  const currentLocation = useLocation().pathname;
  const urlPath = currentLocation.split("/");
  const secondlastURLPath = urlPath[urlPath.length - 2];
  const lastURLPath = urlPath[urlPath.length - 1];

  useEffect(() => {
    const categoryRef = query(collection(db, "categories"));
    const productRef = query(collection(db, "products"));

    onSnapshot(categoryRef, (QuerySnapshot1) => {
      let categories = [];
      QuerySnapshot1.forEach((doc) => {
        categories.push({ ...doc.data(), id: doc.id });
      });
      setCategories(categories);
    });

    if (lastURLPath == "collection") {
      onSnapshot(productRef, (QuerySnapshot2) => {
        let products = [];
        QuerySnapshot2.forEach((doc) => {
          products.push({ ...doc.data(), id: doc.id });
        });
        setProducts(products);
        setfilteredCategories(null);
      });
    } else if (secondlastURLPath === "collection") {
      const collection_type = lastURLPath;
      onSnapshot(categoryRef, (QuerySnapshot1) => {
        let filteredCategoryId = null;

        QuerySnapshot1.forEach((doc) => {
          if (doc.data().category_name === collection_type) {
            filteredCategoryId = doc.id;
            console.log("Retrieved filtering collection:", products);
            return;
          }
        });

        setfilteredCategories(filteredCategoryId);
        console.log("filtered categories :", filteredCategoryId);
      });
    }
  }, [lastURLPath, secondlastURLPath]);

  useEffect(() => {
    if (filteredCategoryId !== null) {
      var specific_productRef = query(
        collection(db, "products"),
        where("categoryID", "==", filteredCategoryId)
      );
      let products = [];
      onSnapshot(specific_productRef, (QuerySnapshot2) => {
        QuerySnapshot2.forEach((doc) => {
          products.push({ ...doc.data(), id: doc.id });
        });
        console.log("Products useEffect triggered");
        console.log("Retrieved products:", products);
        setProducts(products);
      });
    }
  }, filteredCategoryId);

  const [index, setIndex] = useState(0);
  //const [direction, setDirection] = useState("right");
  const itemsPerPage = 4;
  const totalSlides = Math.ceil(products.length / itemsPerPage);

  /*  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
    setDirection(e.direction);
  }; */

  const renderProductCards = (start, end) => {
    return products.slice(start, end).map((product) => (
      <div key={product.id} className="col-lg-3 col-md-4 col-6 card">
        <img
          src={require(`../images/${product.image}`)}
          alt={product.product_name}
          className="card-header"
        />
        <div className="card-body">
          <h5>{product.product_name}</h5>
          <p>{product.description}</p>
          <p>{product.price} MMK</p>
          <Link
            to={`/collection/${product.id}/details`}
            style={{ color: "black" }}
          >
            Details
          </Link>
        </div>
      </div>
    ));
  };

  const renderCarouselItems = () => {
    const items = [];

    for (let i = 0; i < totalSlides; i++) {
      const start = i * itemsPerPage;
      const end = start + itemsPerPage;

      items.push(
        <Carousel.Item key={i}>
          <div className="row collection_series">
            {renderProductCards(start, end)}
          </div>
        </Carousel.Item>
      );
    }

    return items;
  };
  return (
    <div>
      <body>
        <div className="c1 row mx-5">
          {categories.map((category) => (
            <Link
              to={`/collection/${category.category_name}`}
              className="col-lg-2 col-md-2  col-sm-3 "
            >
              <div className={`${category.category_name}`}>
                {category.category_name}
              </div>
            </Link>
          ))}
        </div>
        <div className="row mx-5 my-3">
          {linkValue !== null && (
            <h4 className="col-lg-6 col-md-6 col-4"> {linkValue}</h4>
          )}

          <span className="col-lg-3 col-md-3  col-4 btn border ">
            Default Sorting{" "}
          </span>
          <span className="col-lg-3 col-md-3 col-4 btn border"> Filter </span>
        </div>
        <div className="row collection1 mx-5">
          <div
            className="carousel slide "
            data-bs-ride="carousel"
            id="collection1"
          >
            <ol className="carousel-indicators">
              <li
                data-bs-target="#collection1"
                data-bs-slide-to="0"
                className="active"
              ></li>
              <li data-bs-target="#collection1" data-bs-slide-to="1"></li>
            </ol>
            <Carousel activeIndex={index} direction={"right"}>
              {renderCarouselItems()}
            </Carousel>
          </div>
        </div>
        <marquee>
          <p>
            {" "}
            Free Delivery * Money Back Guarantee * 24 / 7 Support * High Quality
          </p>
        </marquee>
      </body>
    </div>
  );
}

export default Collection;
